import http from "../http-common";

const getAll = () => {
  return http.get("/notes");
};

const get = id => {
  return http.get(`/notes/${id}`);
};

const create = data => {
  return http.post("/tutorials", data);
};

const update = (id, data) => {
  return http.put(`/notes/${id}`, data);
};

const remove = id => {
  return http.delete(`/notes/${id}`);
};

export default {
  getAll,
  get,
  create,
  update,
  remove
};
